import React, {useEffect} from "react";
import "./top-sales.css";
import PropTypes from "prop-types";
import {connect} from "react-redux";
import {Link} from "react-router-dom";
import {getTopSalesData} from "../../actions/inputActions";

const TopSales = ({products, loading, getTopSalesData}) => {
  useEffect(() => {
    getTopSalesData();
    //eslint-disable-next-line
  }, []);

  console.log(loading);
  console.log(products);
  return loading ? (
    <div className="preloader">
      <span></span>
      <span></span>
      <span></span>
      <span></span>
    </div>
  ) : (
    <section className="top-sales">
      <h2 className="text-center">Хиты продаж!</h2>

      <div className="row">
        {products.map((item) => {
          return (
            <div className="col-4">
              <div className="card catalog-item-card">
                <img
                  src={list.image[0]}
                  className="card-img-top img-fluid"
                  alt="Босоножки 'MYER'"
                />
                <div className="card-body">
                  <p className="card-text">{list.title}</p>
                  <p className="card-text">{list.price}руб.</p>
                  <Link to="/products/:1" className="btn btn-outline-primary">
                    Заказать
                  </Link>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </section>
  );
};

TopSales.propTypes = {
  products: PropTypes.array.isRequired,
  loading: PropTypes.bool.isRequired,
};

const mapStateToProps = (state) => ({
  products: state.products,
  loading: state.loading,
});
export default connect(mapStateToProps, {getTopSalesData})(TopSales);
