import React, {useEffect} from "react";
import "./top-sales.css";
import PropTypes from "prop-types";
import {connect} from "react-redux";
import {Link} from "react-router-dom";
import {getTopSalesData} from "../../actions/inputActions";

const TopSales = ({list, loading, getTopSalesData}) => {
  useEffect(() => {
    getTopSalesData();
    //eslint-disable-next-line
  }, []);

  if (loading) {
    return (
      <div className="preloader">
        <span></span>
        <span></span>
        <span></span>
        <span></span>
      </div>
    );
  }
  return (
    <section className="top-sales">
      <h2 className="text-center">Хиты продаж!</h2>

      <div className="row">
        {list !== null ? (
          <p>{list}</p>
        ) : (
          list.map((item) => {
            return (
              <div className="col-4">
                <div className="card catalog-item-card">
                  <img
                    src={list.image[0]}
                    className="card-img-top img-fluid"
                    alt="Босоножки 'MYER'"
                  />
                  <div className="card-body">
                    <p className="card-text">{list.title}</p>
                    <p className="card-text">{list.price}руб.</p>
                    <Link to="/products/:1" className="btn btn-outline-primary">
                      Заказать
                    </Link>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>
    </section>
  );
};

TopSales.propTypes = {
  list: PropTypes.array.isRequired,
  loading: PropTypes.bool.isRequired,
};

const mapStateToProps = (state) => ({
  list: state.list,
  loading: state.loading,
});
export default connect(mapStateToProps, {getTopSalesData})(TopSales);
